import React, { useState } from "react";
import { BsGraphUp } from "react-icons/bs";
// import { Modal } from "react-responsive-modal";
import "react-responsive-modal/styles.css";
import { Line } from "react-chartjs-2";

const DailyChart = function ({ daily }) {
    console.log(daily);
    const [chartData, setChartData] = useState({
        labels: [
            `${daily[0]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
            `${daily[4]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
            `${daily[8]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
            `${daily[12]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
            `${daily[16]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
            `${daily[20]["dt_txt"].match(/\s([0-9]{2})/g)}:00 `,
        ],
        datasets: [
            {
                label: "Temperature graph",
                data: [10, 11, 15, 30, 22],
                background: ["rgba(75,192,192,0.6)"],
                borderWidth: 4,
                // yAxisID: "y-axis-1",
            },
        ],
    });

    const options = {
        responsive: true,
        scales: {
            yAxes: [
                {
                    ticks: {
                        autoSkip: true,
                        maxTicksLimit: 10,
                        beginAtZero: true,
                    },
                    // type: "linear",
                    // display: true,
                    // position: "left",
                    // id: "y-axis-1",
                },
                // ,
                // {
                //     type: "linear",
                //     display: true,
                //     position: "right",
                //     id: "y-axis-2",
                //     gridLines: {
                //         drawOnArea: false,
                //     },
                // },
            ],
        },
    };
    return (
        <>
            {" "}
            <Line data={chartData} options={options} />
        </>
    );
};

export default DailyChart;
