    GoogleMap,
    useLoadScript,
    Marker,
    InfoWindow,
} from "@react-google-maps/api";
import React, { useState, useEffect } from "react";

const UkMap = ({ cities }) => {
    console.log(cities != []);
    const libraries = ["places"];

