import React, { useState, useEffect } from "react";
import City from "../Components/City";
import SearchForm from "../Components/SearchForm";
import {
    getFavourites,
    postFavourite,
    deleteFavourite,
} from "../services/FavouriteService";

const WeatherContainer = () => {
    const [cities, setCities] = useState([]);
    const [selectedCity, setSelectedCity] = useState(null);
    const [favourites, setFavourites] = useState([]);

    useEffect(() => {
        if (selectedCity != null) {
            getCities(selectedCity);
        }
    }, [selectedCity]);

    // useEffect(() => {
    //     if (favourites != []) {
    //         setFavourites(favourites);
    //     }
    // }, [favourites]);

    // const setFavourites = function (favourites){
    //     // api call
    //     postFavourite(favourites)

    // }

    useEffect(() => {
        getFavouriteCities(favourites);
    }, [favourites]);

    const getFavouriteCities = function (favourites) {
        console.log(favourites);
        Promise.all(
            favourites.map(
                (eachCity) => console.log(eachCity.name)
                // fetch(
                //     `http://pro.openweathermap.org/data/2.5/forecast/hourly?q=${eachCity}&appid=3031aac4ff517ddfc83b94a403d374b0`
                // )
            )
        )
            .then((res) => {
                return Promise.all(
                    res.map(function (res) {
                        return res.json();
                    })
                );
            })
            .then((result) => console.log(result));
    };

    const getCities = function (selectedCity) {
        const cityApi = `http://pro.openweathermap.org/data/2.5/forecast/hourly?q=${selectedCity}&appid=3031aac4ff517ddfc83b94a403d374b0`;
        const forecastApi = `http://api.openweathermap.org/data/2.5/forecast?q=${selectedCity}&appid=1a9a20046a26886e891582ce46507106`;
        Promise.all([fetch(cityApi), fetch(forecastApi)])
            .then((res) => {
                return Promise.all(
                    res.map(function (res) {
                        return res.json();
                    })
                );
            })
            .then((result) =>
                setCities([
                    ...cities,
                    { daily: result[0], forecast: result[1] },
                ])
            );
    };

    const onCitySubmit = function (city) {
        console.log(city);
        setSelectedCity(city);
    };

    const onClick = function (favourite) {
