import {
    GoogleMap,
    useLoadScript,
    Marker,
    InfoWindow,
} from "@react-google-maps/api";

const UkMap = ({ cities }) => {
    console.log("cities", cities);

    // const lng = cities[0];
    // const city = cityName[0];
    const options = {};
    let result = cities.map((eachCity) => {
        console.log("city", eachCity["forecast"]["city"]["coord"]["lat"]);
        const lat = eachCity["forecast"]["city"]["coord"]["lat"];
        const lng = eachCity["forecast"]["city"]["coord"]["lng"];
        let center = {
            lat: lat,
            lng: lng,
        };
        const containerStyle = {
            position: "static",
            margin: "34px",
            width: "60vw",
            height: "60vh",
        };
        return (
            <GoogleMap
                mapContainerStyle={containerStyle}
                zoom={12}
                center={center}
                options={options}
                // onClick={handleMap}
            />
        );
    });

    return (
        <>
            <div>{cities != null ? result : null}</div>
        </>
    );
};

export default UkMap;
