import {
    GoogleMap,
    useLoadScript,
    Marker,
    InfoWindow,
} from "@react-google-maps/api";
import React, { useState } from "react";

const UkMap = ({ cities }) => {
    console.log("cities", cities);
    const libraries = ["places"];

    const { isLoaded, loadError } = useLoadScript({
        googleMapsApiKey: process.env.React_APP_Google_MAPS_API_KEY,
        libraries,
    });
    // const lng = cities[0];
    // const city = cityName[0];
    const options = {};
    let result = cities.map((eachCity) => {
        console.log("city", eachCity["forecast"]["city"]["coord"]["lon"]);
        const lat1 = eachCity["forecast"]["city"]["coord"]["lat"];
        const lng = eachCity["forecast"]["city"]["coord"]["lon"];
        let center = {
            lat: lat1,
            lng: lng,
        };

        const containerStyle = {
            position: "static",
            margin: "34px",
            width: "60vw",
            height: "60vh",
        };
        return (
            <GoogleMap
                mapContainerStyle={containerStyle}
                zoom={8}
                center={center}
                options={options}
                // onClick={handleMap}
            />
        );
    });

    return (
        <>
            <div>{cities != [] ? result : null}</div>
        </>
    );
};

export default UkMap;
